mapper.select:
UPDATE member set 
pw =#{pw}, name=#{name}, phone=#{phone}, zipcode=#{zipcode}, addr1=#{addr1}, addr2=#{addr2}, email=#{email} 
where id=#{id}

choose.when:

UPDATE curiboard SET title=#{title},writer=#{writer},

	filename = #{filename},
	filesize = #{filesize},

	content=#{content}
	 where bno=#{bno}

choose.otherwise:

UPDATE curiboard SET title=#{title},writer=#{writer},

	filename = #{filename},
	filesize = #{filesize}

	where bno=#{bno}

mapper.select:
SELECT * FROM curireply
WHERE bno = #{bno}
ORDER BY rno ASC



SELECT * FROM curi_PRO_REPLY
WHERE P_CODE = #{p_code}
ORDER BY rno ASC
